
$( "#buttonOrange" ).click(function() {
  $('.hero').css("background-color", "#f7941e");
  $('.heroFontColorHeader').css("color", "black");
  $('.heroFontColorDescription').css("color", "black");
});

$( "#buttonDarkGrey" ).click(function() {
  $('.hero').css("background-color", "#4a4a4a");
  $('.heroFontColorHeader').css("color", "#f7941e");
  $('.heroFontColorDescription').css("color", "white");
});

$( "#buttonLightGrey" ).click(function() {
  $('.hero').css("background-color", "#ebeceb");
  $('.heroFontColorHeader').css("color", "black");
  $('.heroFontColorDescription').css("color", "black");
});

$( "#buttonMiddleGrey" ).click(function() {
  $('.hero').css("background-color", "#bfc2c9");
  $('.heroFontColorHeader').css("color", "white");
  $('.heroFontColorDescription').css("color", "black");
});

$( "#buttonWhite" ).click(function() {
  $('.hero').css("background-color", "#fff");
  $('.heroFontColorHeader').css("color", "#f7941e");
  $('.heroFontColorDescription').css("color", "black");
});

